command:
// Standard Hotbar macro to apply an active effect from a given actor (default Zone Effects) to a selected drawing
// Change the actor name as required 


let effectActor = game.actors.getName("<name here>")
let effects = effectActor.effects.contents
let drawing = canvas.drawings.controlled[0]
let content
for ( let effect of effects) {
content  += `<option value=${effect.id}>${effect.label}</option>`;
}
new Dialog({
    title: 'Example',
    content: `
          <form class="flexcol">
            <div class="form-group">
              <select id="effect">
              ${content}
              </select>
            </div>
          </form>
        `,
    buttons: {
      apply: {
          label: "Apply",
          callback: async (html) => {
            let effectId = html.find('#effect').val();
            let effect = effects.find(i => i.id === effectId)
            effect.origin = `Actor.${effectActor.id}`
            await drawing.document.setFlag("ActiveAuras", "IsAura", [effect.data])
            AAhelpers.UserCollateAuras(canvas.scene.id, true, false, "drawingAdd")
          }
      }
    }
}).render(true)
